import React, { useEffect, useState } from 'react';

function App() {
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = () => {
      fetch('http://localhost:3000/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
        body: JSON.stringify({
          query: `
            query {
              data(orderBy: id, first: 1) {
                nb_block
                starknet_block
                eventCount
              }
            }
          `
        })
      })
      .then(r => r.json())
      .then(data => {
        console.log('API response:', data); // Etape 4: Vérifiez la réponse de l'API
        setData(data.data.data);
      })
      .catch(error => console.error('Error fetching data:', error)); // Etape 3: Gérez les erreurs
    };

    fetchData(); // Fetch data immediately
    const intervalId = setInterval(fetchData, 1000); // Fetch data every 3 seconds

    return () => {
      clearInterval(intervalId); // Clean up on unmount
    };
  }, []);

  if (!data) {
    return <div>Loading...</div>;
  }

  return (
    <div style={{ backgroundColor: 'lightpurple', height: '100vh', color: 'white', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
      <h1 style={{ textShadow: '2px 2px black' }}>Alakazam</h1>
      <table style={{ color: 'black', border: '2px solid black', borderRadius: '5px', backgroundColor: 'white' }}>
        <thead>
          <tr>
            <th>Id</th>
            <th>Name</th>
            <th>Layer</th>
            <th>Number Total of Block</th>
            <th>Actual Block of Indexing</th>
            <th>Number of Event</th>
          </tr>
        </thead>
        <tbody>
          {data.map((d, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>Starknet</td>
              <td>L2</td>
              <td>{d.starknet_block}</td>
              <td>{d.nb_block}</td>
              <td>{d.eventCount}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
