import React, { useEffect, useState } from 'react';

function App() {
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = () => {
      fetch('http://localhost:3000/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
        body: JSON.stringify({
          query: `
            query {
              data(orderBy: id, first: 1) {
                nb_block
                starknet_block
                eventCount
                goerliblock
              }
            }
          `
        })
      })
      .then(r => r.json())
      .then(data => {
        console.log('API response:', data);
        setData(data.data.data);
      })
      .catch(error => console.error('Error fetching data:', error));
    };

    fetchData();
    const intervalId = setInterval(fetchData, 1000);

    return () => {
      clearInterval(intervalId);
    };
  }, []);

  if (!data) {
    return <div>Loading...</div>;
  }

  const tableStyle = {
    color: 'black',
    border: '2px solid black',
    borderRadius: '5px',
    backgroundColor: 'white',
    width: '80%',
    borderCollapse: 'separate',
    borderSpacing: '0',
    margin: '20px 0'
  };

  const cellStyle = {
    padding: '10px',
    border: '1px solid black'
  };

  return (
    <div style={{ backgroundColor: '#6a0dad', height: '100vh', color: 'white', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
      <h1 style={{ textShadow: '2px 2px black' }}>Alakazam</h1>
      <table style={tableStyle}>
        <thead>
          <tr>
            <th style={cellStyle}>Id</th>
            <th style={cellStyle}>Name</th>
            <th style={cellStyle}>Layer</th>
            <th style={cellStyle}>Number Total of Block</th>
            <th style={cellStyle}>Actual Block of Indexing</th>
            <th style={cellStyle}>Number of Event</th>
          </tr>
        </thead>
        <tbody>
          {data.map((d, index) => (
            <>
              <tr key={index}>
                <td style={cellStyle}>{index + 1}</td>
                <td style={cellStyle}>Starknet</td>
                <td style={cellStyle}>L2</td>
                <td style={cellStyle}>{d.starknet_block}</td>
                <td style={cellStyle}>{d.nb_block}</td>
                <td style={cellStyle}>{d.eventCount}</td>
              </tr>
              <tr key={index+100}>
                <td style={cellStyle}>2</td>
                <td style={cellStyle}>Goerli Starknet</td>
                <td style={cellStyle}>L2 (testnet)</td>
                <td style={cellStyle}>{d.goerliblock}</td>
                <td style={cellStyle}>soon</td>
                <td style={cellStyle}>soon</td>
              </tr>
            </>
          ))}
        </tbody>
      </table>

    </div>
  );
}

export default App;
